import React, { useState, useContext } from "react";
import { useNavigate, Link } from "react-router-dom";
import axios from "axios";
import { AuthContext } from "../../../context/AuthContext";
import "../../Auth.css"; // Новый стиль

const Login = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");
  const { login } = useContext(AuthContext);
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError("");

    try {
      console.log("Отправляем запрос на вход:", { email, password });

      const response = await axios.post(
        "http://api/login",
        { email, password },
        { withCredentials: true } // если сервер использует куки
      );

      console.log("Успешный вход:", response.data);

      login(response.data.user); // вызвать login из AuthContext
      localStorage.setItem("token", response.data.token); // сохранить токен

      navigate("/profile"); // перенаправить
    } catch (err) {
      console.error("Ошибка входа:", err.response?.data?.message || err.message);
      setError(err.response?.data?.message || "Ошибка входа");
    }
  };

  return (
    <div className="auth-container">
      <div className="auth-card shadow">
        <h2 className="auth-title">Вход</h2>
        {error && <p className="auth-error">{error}</p>}
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label htmlFor="email">Email</label>
            <input
              type="email"
              id="email"
              className="form-input"
              placeholder="Введите ваш email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
            />
          </div>
          <div className="form-group">
            <label htmlFor="password">Пароль</label>
            <input
              type="password"
              id="password"
              className="form-input"
              placeholder="Введите ваш пароль"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
            />
          </div>
          <button type="submit" className="auth-button">
            Войти
          </button>
        </form>
        <p className="auth-switch">
          Нет аккаунта? <Link to="/registration" className="auth-link">Зарегистрироваться</Link>
        </p>
      </div>
    </div>
  );
};

export default Login;
