import { useEffect, useState } from 'react';
import { useSearchParams } from 'react-router-dom';
import axios from 'axios';

export default function ProfilePage() {
  const [searchParams] = useSearchParams();
  const userId = searchParams.get('user_id');
  const [user, setUser] = useState(null);
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchProfile = async () => {
      try {
        const profileRes = await axios.get(`/api/profile${userId ? `?user_id=${userId}` : ''}`);
        setUser(profileRes.data.user);

        const ordersUrl = userId
          ? `/api/profile/${userId}/public-orders`
          : '/api/profile/orders';

        const ordersRes = await axios.get(ordersUrl);
        setOrders(ordersRes.data);
      } catch (err) {
        console.error(err);
      } finally {
        setLoading(false);
      }
    };

    fetchProfile();
  }, [userId]);

  if (loading) return <p>Загрузка профиля...</p>;
  if (!user) return <p>Профиль не найден</p>;

  return (
    <div className="p-4">
      <h2 className="text-xl font-bold">Профиль пользователя: {user.name}</h2>
      <p>Email: {user.email}</p>
      <p>Город: {user.city}</p>
      <p>Количество заказов: {user.orders_count ?? orders.length}</p>

      <h3 className="mt-4 text-lg font-semibold">Заказы:</h3>
      <ul className="mt-2 space-y-2">
        {orders.map(order => (
          <li key={order.id} className="border p-2 rounded shadow">
            <strong>{order.title}</strong><br />
            {order.description}<br />
            <small>Цена: {order.price}</small>
          </li>
        ))}
      </ul>
    </div>
  );
}
