import { useEffect, useState } from 'react';
import { useSearchParams } from 'react-router-dom';
import axios from 'axios';

export default function ProfilePage() {
  const [searchParams] = useSearchParams();
  const userId = searchParams.get('user_id');
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchProfile = async () => {
      try {
        setLoading(true);
        setError(null); // Очистить ошибки при новом запросе

        // Новый API запрос для получения профиля
        const profileRes = await axios.get(`/api/user/profile/${userId}/profile`);
        setUser(profileRes.data.user); // Предполагаем, что в ответе данные профиля лежат в `user`
      } catch (err) {
        console.error(err);
        setError('Ошибка при загрузке данных. Попробуйте снова позже.');
      } finally {
        setLoading(false);
      }
    };

    if (userId) {
      fetchProfile();
    }
  }, [userId]);

  if (loading) return <p>Загрузка профиля...</p>;
  if (error) return <p className="text-red-500">{error}</p>;
  if (!user) return <p>Профиль не найден.</p>;

  return (
    <div className="p-4">
      <h2 className="text-xl font-bold">Профиль пользователя: {user.firstname} {user.lastname}</h2>
      <p>Email: {user.email}</p>
      <p>Город: {user.city}</p>
      <p>Телефон: {user.phone}</p>

      {/* Если необходимо вывести дополнительные данные, можно добавить их здесь */}
    </div>
  );
}
