import React, { useState, useEffect } from "react";
import axios from "axios";
import "bootstrap/dist/css/bootstrap.min.css";
import "./Profile.css";

const Profile = () => {
  const [orders, setOrders] = useState([]);
  const [message, setMessage] = useState("");
  const [order, setOrder] = useState({ title: "", description: "", price: "" });
  const [isEditing, setIsEditing] = useState(false);
  const [editOrderId, setEditOrderId] = useState(null);

  const token = localStorage.getItem("token");

  useEffect(() => {
    fetchOrders();
  }, []);

  const fetchOrders = async () => {
    try {
      const response = await axios.get("/api/user/profile/orders", {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      setOrders(response.data);
    } catch (error) {
      console.error("Ошибка при загрузке заказов:", error);
      setMessage("Не удалось загрузить заказы.");
    }
  };

  const handleDeleteOrder = async (order) => {
    setMessage("");
    try {
      await axios.delete(`/api/user/profile/orders/${order.id}`, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      setOrders(orders.filter((o) => o.id !== order.id));
      setMessage("Заказ успешно удален.");
    } catch (error) {
      console.error("Ошибка при удалении заказа:", error);
      setMessage("Ошибка при удалении заказа.");
    }
  };

  const handleEdit = (o) => {
    setOrder({ title: o.title, description: o.description, price: o.price });
    setIsEditing(true);
    setEditOrderId(o.id);
    setMessage("");
    window.scrollTo({ top: 0, behavior: "smooth" }); // прокрутка к форме
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setMessage("");

    const config = {
      headers: {
        "Content-Type": "application/json",
        Authorization: `Bearer ${token}`,
      },
    };

    try {
      if (isEditing) {
        await axios.put(`/api/user/profile/orders/${editOrderId}`, order, config);
        setMessage("Заказ успешно обновлён!");
      } else {
        await axios.post("/api/user/profile/orders", order, config);
        setMessage("Заказ успешно создан!");
      }

      setOrder({ title: "", description: "", price: "" });
      setIsEditing(false);
      setEditOrderId(null);
      fetchOrders();
    } catch (err) {
      console.error("Ошибка при сохранении заказа:", err);
      setMessage("Произошла ошибка при сохранении заказа.");
    }
  };

  return (
    <div className="profile-page">
      <div className="container py-5">
        <h2 className="section-title">{isEditing ? "Редактировать заказ" : "Создать заказ"}</h2>

        <div className="create-order-card mb-5">
          {message && <div className="alert alert-info">{message}</div>}
          <form onSubmit={handleSubmit}>
            <div className="mb-3">
              <label className="form-label">Название</label>
              <input
                type="text"
                className="form-control"
                value={order.title}
                onChange={(e) => setOrder({ ...order, title: e.target.value })}
                required
              />
            </div>
            <div className="mb-3">
              <label className="form-label">Описание</label>
              <textarea
                className="form-control"
                value={order.description}
                onChange={(e) => setOrder({ ...order, description: e.target.value })}
                required
              />
            </div>
            <div className="mb-3">
              <label className="form-label">Цена</label>
              <input
                type="text"
                className="form-control"
                value={order.price}
                onChange={(e) => setOrder({ ...order, price: e.target.value })}
                required
              />
            </div>
            <button type="submit" className="btn btn-primary">
              {isEditing ? "Сохранить изменения" : "Создать заказ"}
            </button>
          </form>
        </div>

        <h3 className="section-title">Мои заказы</h3>
        {orders.length === 0 ? (
          <p className="no-orders">У вас пока нет заказов.</p>
        ) : (
          <div className="row g-4">
            {orders.map((order) => (
              <div className="col-md-4" key={order.id}>
                <div className="order-card">
                  <img
                    src={order.image || "https://via.placeholder.com/400x200"}
                    alt={order.title}
                    className="order-image"
                  />
                  <div className="order-info">
                    <h5 className="order-title">{order.title}</h5>
                    <p className="order-description">{order.description}</p>
                    <p className="order-price">{order.price} тг</p>
                    <div className="order-actions d-flex justify-content-between">
                      <button
                        className="btn btn-warning custom-btn me-2"
                        onClick={() => handleEdit(order)}
                      >
                        Редактировать
                      </button>
                      <button
                        className="btn btn-danger custom-btn"
                        onClick={() => handleDeleteOrder(order)}
                      >
                        Удалить
                      </button>
                    </div>
                  </div>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default Profile;
