import React, { useState, useEffect, useContext } from "react";
import { useParams, Link } from "react-router-dom";
import { AuthContext } from "../../../context/AuthContext";
import "bootstrap/dist/css/bootstrap.min.css";
import "./FindOrder.css";

const FindOrder = () => {
  const { id } = useParams(); // Извлекаем ID заказа из URL
  const { isAuthenticated } = useContext(AuthContext); // Проверяем, авторизован ли пользователь
  const [order, setOrder] = useState(null); // Состояние для заказа(ов)
  const [orders, setOrders] = useState([]); // Состояние для списка заказов
  const [isListView, setIsListView] = useState(!id); // Если ID нет, отображается список заказов

  // Функция для загрузки заказов
  const fetchOrders = async () => {
    try {
      const response = await fetch('/api/user/profile/orders', {
        method: 'GET',
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('token')}`,
        },
      });
      const data = await response.json();
      if (Array.isArray(data)) {
        setOrders(data);
      }
    } catch (error) {
      console.error('Ошибка при загрузке заказов:', error);
    }
  };

  // Загружаем заказы, если мы в режиме списка
  useEffect(() => {
    if (isListView) {
      fetchOrders(); // Загрузка заказов
    } else {
      const selectedOrder = orders.find((order) => order.id === parseInt(id));
      setOrder(selectedOrder); // Устанавливаем выбранный заказ
    }
  }, [id, isListView, orders]);

  // Если данные заказа не загружены, отображаем "Загрузка..."
  if (!orders.length && isListView) return <div className="text-white">Загрузка...</div>;
  if (!order && !isListView) return <div className="text-white">Загрузка...</div>;

  return (
    <div className="find-order-page">
      <div className="container py-5">
        {isListView ? (
          // Режим списка заказов
          <>
            <h2 className="section-title">Найти заказ</h2>
            {orders.length === 0 ? (
              <p className="text-white">Нет доступных заказов.</p>
            ) : (
              <div className="row g-4">
                {orders.map((ord) => (
                  <div className="col-md-4" key={ord.id}>
                    <div className="order-card">
                      <img
                        src={ord.image || "default-image-url.jpg"} // Если нет картинки, ставим дефолтное изображение
                        alt={ord.title}
                        className="order-image"
                      />
                      <div className="order-info">
                        <h5 className="order-title">{ord.title}</h5>
                        <p className="order-description">{ord.description}</p>
                        <p className="order-price">{ord.price} тг</p>
                        <Link
                          to={`/order/${ord.id}`}
                          className="btn btn-primary"
                          onClick={() => setIsListView(false)} // Переключаем в режим одного заказа
                        >
                          Подробнее
                        </Link>
                      </div>
                    </div>
                  </div>
                ))}
              </div>
            )}
          </>
        ) : (
          // Режим одного заказа
          <>
            <h2 className="section-title">{order.title}</h2>
            <div className="order-details-card">
              <h5 className="card-title">Информация по заданию</h5>
              <p>
                <strong>Описание:</strong> {order.description}
              </p>
              <p>
                <strong>Стоимость:</strong> {order.price} тг
              </p>
              {/* Добавьте остальные данные из объекта заказа */}
              <p>
                <strong>Дата создания:</strong> {new Date(order.created_at).toLocaleString()}
              </p>
              <p>
                <strong>Дата обновления:</strong> {new Date(order.updated_at).toLocaleString()}
              </p>
              {isAuthenticated ? (
                <button className="btn btn-primary mt-3">Откликнуться</button>
              ) : (
                <div className="alert alert-warning mt-3">
                  Заказ выполнять могут только авторизованные пользователи.{" "}
                  <Link to="/login">Войти</Link> или{" "}
                  <Link to="/registration">Зарегистрироваться</Link>.
                </div>
              )}
            </div>
          </>
        )}
      </div>
    </div>
  );
};

export default FindOrder;
