import React, { useEffect, useState } from "react";
import axios from "axios";

const Notifications = () => {
  const [notifications, setNotifications] = useState([]);
  const [loading, setLoading] = useState(true);
  const token = localStorage.getItem("token");

  useEffect(() => {
    const fetchNotifications = async () => {
      try {
        const response = await axios.get("/api/user/notifications", {
          headers: { Authorization: `Bearer ${token}` },
        });
        setNotifications(response.data.notifications);
      } catch (error) {
        console.error("Ошибка при загрузке уведомлений:", error);
      } finally {
        setLoading(false);
      }
    };

    fetchNotifications();
  }, [token]);

  const handleApproveRequest = async (orderId, requestId) => {
    try {
      const response = await axios.post(
        `/api/orders/${orderId}/approve-request/${requestId}`,
        {},
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );
      alert(response.data.message);

      // После успешного принятия обновляем уведомления
      setNotifications((prev) =>
        prev.filter((n) => !(n.order_id === orderId && n.id === requestId))
      );
    } catch (error) {
      console.error("Ошибка при подтверждении запроса:", error);
      alert("Произошла ошибка при подтверждении запроса.");
    }
  };

  if (loading) {
    return <div>Загрузка уведомлений...</div>;
  }

  if (notifications.length === 0) {
    return <div>У вас пока нет уведомлений.</div>;
  }

  return (
    <div className="container py-5">
      <h2>Мои уведомления</h2>
      <ul className="list-group">
        {notifications.map((notification) => (
          <li key={notification.id} className="list-group-item d-flex justify-content-between align-items-center">
            <div>
              Новый отклик на заказ: <strong>{notification.order_title}</strong><br />
              От пользователя: <strong>{notification.user_name}</strong>
            </div>
            <button
              className="btn btn-success"
              onClick={() => handleApproveRequest(notification.order_id, notification.id)}
            >
              Принять отклик
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Notifications;
