import React, { useState, useEffect } from "react";
import axios from "axios";
import "./ChatWindow.css";

const ChatWindow = ({ orderId, currentUser, token }) => {
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState("");

  useEffect(() => {
    const fetchMessages = async () => {
      try {
        const response = await fetch(`/api/orders/${orderId}/messages`, {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });
        const data = await response.json();
        setMessages(data);
      } catch (error) {
        console.error("Ошибка загрузки сообщений:", error);
      }
    };

    fetchMessages();
  }, [orderId, token]);

  const handleSendMessage = async () => {
    if (!newMessage.trim()) return;

    const messageData = {
      content: newMessage,
      sender: currentUser?.username || "Вы", // подставляем имя текущего пользователя
    };

    try {
      const response = await fetch(`/api/orders/${orderId}/messages`, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify(messageData),
      });

      const newMsg = await response.json();
      setMessages([...messages, newMsg]);
      setNewMessage("");
    } catch (error) {
      console.error("Ошибка при отправке сообщения:", error);
    }
  };

  return (
    <div className="chat-container">
      <div className="chat-header">Чат по заказу #{orderId}</div>
      <div className="chat-messages">
        {messages.map((msg, index) => (
          <div className="chat-message" key={index}>
            <div className="sender">{msg.sender || "Пользователь"}</div>
            <div className="message">{msg.content}</div>
            <div className="time">{msg.time || "Неизвестно"}</div>
          </div>
        ))}
      </div>
      <div className="chat-input">
        <input
          type="text"
          value={newMessage}
          placeholder="Введите сообщение..."
          onChange={(e) => setNewMessage(e.target.value)}
        />
        <button onClick={handleSendMessage}>Отправить</button>
      </div>
    </div>
  );
};

export default ChatWindow;
