// src/components/Order.js
import React, { useEffect, useState } from "react";
import axios from "axios";
import { useParams, useNavigate } from "react-router-dom";
import "bootstrap/dist/css/bootstrap.min.css";
import "./Orders.css";

const Order = () => {
  const { id } = useParams(); // Получаем ID заказа из URL
  const [order, setOrder] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const navigate = useNavigate();

  const token = localStorage.getItem("token");

  useEffect(() => {
    const fetchOrder = async () => {
      try {
        const response = await axios.get(`/api/user/profile/orders/${id}`, {
          headers: { Authorization: `Bearer ${token}` },
        });
        setOrder(response.data);
      } catch (err) {
        console.error("Ошибка при загрузке заказа:", err);
        setError("Не удалось загрузить заказ.");
      } finally {
        setLoading(false);
      }
    };

    if (id) fetchOrder();
  }, [id]);

  return (
    <div className="create-order-page">
      <div className="container py-5">
        <button className="btn btn-link mb-4" onClick={() => navigate(-1)}>
          ← Назад
        </button>
        <h2 className="section-title">Просмотр заказа</h2>

        {loading ? (
          <p>Загрузка...</p>
        ) : error ? (
          <div className="alert alert-danger">{error}</div>
        ) : order ? (
          <div className="order-card">
            <img
              src="https://via.placeholder.com/600x300"
              alt="order"
              className="order-image"
            />
            <div className="order-info">
              <h5 className="order-title">{order.title}</h5>
              <p className="order-description">{order.description}</p>
              <p className="order-price">{order.price} тг</p>
              <p className="order-date">
                Создан: {new Date(order.created_at).toLocaleDateString()}
              </p>
            </div>
          </div>
        ) : (
          <p>Заказ не найден.</p>
        )}
      </div>
    </div>
  );
};

export default Order;
