import React, { useState, useEffect } from "react";
import axios from "axios";
import "bootstrap/dist/css/bootstrap.min.css";
import "./Orders.css";

const Orders = () => {
  const [order, setOrder] = useState({ title: "", description: "", price: "" });
  const [orders, setOrders] = useState([]);
  const [message, setMessage] = useState("");
  const [isEditing, setIsEditing] = useState(false);
  const [editOrderId, setEditOrderId] = useState(null);

  const fetchOrders = async () => {
    try {
      const response = await axios.get("/api/user/profile/orders", {
        headers: {
          Authorization: `Bearer ${localStorage.getItem("token")}`,
        },
      });
      setOrders(response.data);
    } catch (err) {
      console.error("Ошибка при загрузке заказов:", err);
    }
  };

  useEffect(() => {
    fetchOrders();
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setMessage("");

    const config = {
      headers: {
        "Content-Type": "application/json",
        Authorization: `Bearer ${localStorage.getItem("token")}`,
      },
    };

    try {
      if (isEditing) {
        await axios.put(`/api/user/profile/orders/${editOrderId}`, order, config);
        setMessage("Заказ успешно обновлён!");
      } else {
        await axios.post("/api/user/profile/orders", order, config);
        setMessage("Заказ успешно создан!");
      }

      // Очистить форму
      setOrder({ title: "", description: "", price: "" });
      setIsEditing(false);
      setEditOrderId(null);
      fetchOrders(); // обновить список
    } catch (err) {
      console.error("Ошибка при отправке:", err);
      setMessage("Произошла ошибка при сохранении заказа.");
    }
  };

  const handleEdit = (o) => {
    setOrder({ title: o.title, description: o.description, price: o.price });
    setIsEditing(true);
    setEditOrderId(o.id);
    setMessage("");
  };

  return (
    <div className="create-order-page">
      <div className="container py-5">
        <h2 className="section-title">{isEditing ? "Редактировать заказ" : "Выставить заказ"}</h2>
        <div className="create-order-card">
          {message && <div className="alert alert-info">{message}</div>}
          <form onSubmit={handleSubmit}>
            <div className="mb-3">
              <label className="form-label">Название</label>
              <input
                type="text"
                className="form-control"
                value={order.title}
                onChange={(e) => setOrder({ ...order, title: e.target.value })}
                placeholder="Введите название заказа"
                required
              />
            </div>
            <div className="mb-3">
              <label className="form-label">Описание</label>
              <textarea
                className="form-control"
                value={order.description}
                onChange={(e) => setOrder({ ...order, description: e.target.value })}
                placeholder="Опишите заказ"
                required
              />
            </div>
            <div className="mb-3">
              <label className="form-label">Цена</label>
              <input
                type="text"
                className="form-control"
                value={order.price}
                onChange={(e) => setOrder({ ...order, price: e.target.value })}
                placeholder="Введите цену (например, 100 000 тг)"
                required
              />
            </div>
            <button type="submit" className="btn btn-primary">
              {isEditing ? "Сохранить изменения" : "Выставить заказ"}
            </button>
          </form>
        </div>

        <hr className="my-5" />
        <h3 className="section-title">Мои заказы</h3>
        {orders.length === 0 && <p>У вас пока нет заказов.</p>}
        {orders.map((o) => (
          <div key={o.id} className="card mb-3 p-3">
            <h5>{o.title}</h5>
            <p>{o.description}</p>
            <p><strong>Цена:</strong> {o.price}</p>
            <button className="btn btn-sm btn-warning me-2" onClick={() => handleEdit(o)}>
              Редактировать
            </button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Orders;
