import React, { useState, useEffect } from "react";
import axios from "axios";
import "./ChatWindow.css";

const ChatWindow = ({ orderId, onClose }) => {
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState("");
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const [recipientId, setRecipientId] = useState(null);

  const token = localStorage.getItem("token");

  // Проверка на авторизацию
  if (!token) {
    return <div className="alert alert-warning">Вы не авторизованы.</div>;
  }

  // Получение сообщений чата
  useEffect(() => {
    const fetchMessages = async () => {
      if (!orderId) return; // <-- Условие перемещено внутрь
      setLoading(true);
      try {
        const response = await axios.get(`/api/user/order/chat/${orderId}`, {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });
  
        if (response.data.success) {
          const chatData = response.data.data;
          const fetchedMessages = chatData.messages || chatData;
          setMessages(fetchedMessages);
  
          if (fetchedMessages.length > 0) {
            const firstMsg = fetchedMessages[0];
            const currentUserId = getUserIdFromToken(token);
            const recipient =
              firstMsg.sender_id === currentUserId
                ? firstMsg.recipient_id
                : firstMsg.sender_id;
            setRecipientId(recipient);
          }
        } else {
          setError("Не удалось загрузить сообщения.");
        }
      } catch (err) {
        console.error(err);
        setError("Ошибка загрузки чата.");
      } finally {
        setLoading(false);
      }
    };
  
    fetchMessages(); // всегда вызывается
  }, [orderId, token]); // и обязательно включи `token` в зависимости

  // Отправка нового сообщения
  const handleSendMessage = async (e) => {
    e.preventDefault();
    if (!newMessage.trim() || !recipientId) return;

    try {
      const response = await axios.post(
        `/api/user/order/chat/${orderId}/message`,
        {
          content: newMessage,
          recipient_id: recipientId,
        },
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );

      if (response.data.success) {
        const newMsg = response.data.data;
        setMessages((prev) => [...prev, newMsg]);
        setNewMessage("");
      } else {
        setError("Не удалось отправить сообщение.");
      }
    } catch (err) {
      console.error(err);
      setError("Ошибка при отправке.");
    }
  };

  return (
    <div className="chat-window">
      <div className="chat-header">
        <h5>Чат по заказу #{orderId}</h5>
        <button className="btn btn-close" onClick={onClose}></button>
      </div>

      <div className="chat-body">
        {loading ? (
          <p>Загрузка...</p>
        ) : (
          <div className="chat-messages">
            {messages.length > 0 ? (
              messages.map((msg) => (
                <div
                  key={msg.id}
                  className={`message ${
                    msg.sender_id === getUserIdFromToken(token)
                      ? "message-sent"
                      : "message-received"
                  }`}
                >
                  <strong>
                    {msg.sender_id === getUserIdFromToken(token)
                      ? "Вы"
                      : msg.sender?.name || `Пользователь #${msg.sender_id}`}{" "}
                    → {msg.recipient?.name || "Получатель"}:
                  </strong>
                  <p>{msg.content}</p>
                  <small className="message-timestamp">
                    {new Date(msg.created_at).toLocaleTimeString([], {
                      hour: "2-digit",
                      minute: "2-digit",
                    })}
                  </small>
                </div>
              ))
            ) : (
              <div className="no-messages">Сообщений пока нет.</div>
            )}
          </div>
        )}

        <form onSubmit={handleSendMessage} className="message-form mt-2">
          <input
            type="text"
            className="form-control"
            value={newMessage}
            onChange={(e) => setNewMessage(e.target.value)}
            placeholder="Введите сообщение..."
          />
          <button type="submit" className="btn btn-primary mt-2 w-100">
            Отправить
          </button>
        </form>

        {error && <div className="alert alert-danger mt-2">{error}</div>}
      </div>
    </div>
  );
};

// Вспомогательная функция: получить user_id из токена
function getUserIdFromToken(token) {
  try {
    const payload = JSON.parse(atob(token.split(".")[1]));
    return payload.sub;
  } catch (e) {
    return null;
  }
}

export default ChatWindow;
