import React, { useEffect, useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const Notifications = () => {
  const [requests, setRequests] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    axios.get('http://localhost:8000/api/notifications', {
      withCredentials: true
    })
      .then(res => setRequests(res.data))
      .catch(err => console.error(err));
  }, []);

  const handleApprove = (orderId, requestId) => {
    axios.post(`/api/user/profile/orders/${orderId}/approve-request/${requestId}`)
      .then(res => {
        alert(res.data.message);
        // Перезагрузить список откликов
        setRequests(prev => prev.filter(r => r.id !== requestId));
        navigate(`/profile/orders/${orderId}`); // Перейти на заказ
      })
      .catch(err => {
        console.error("Ошибка при принятии запроса:", err);
        alert("Ошибка при принятии запроса");
      });
  };

  return (
    <div>
      <h2>Уведомления</h2>
      {requests.length === 0 ? (
        <p>Нет новых запросов</p>
      ) : (
        <ul>
          {requests.map(req => (
            <li key={req.id} style={{ marginBottom: "20px" }}>
              <strong>{req.user.name}</strong> хочет выполнить заказ: <em>{req.order.title}</em><br />
              <button onClick={() => handleApprove(req.order.id, req.id)}>
                Принять запрос
              </button>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default Notifications;
