import React, { useState, useEffect, useContext } from "react";
import { Link } from "react-router-dom";
import { AuthContext } from "../../../context/AuthContext";
import axios from 'axios';
import "bootstrap/dist/css/bootstrap.min.css";
import "./FindOrder.css";

const FindOrder = () => {
  const { isAuthenticated } = useContext(AuthContext); // Проверяем, авторизован ли пользователь
  const [orders, setOrders] = useState([]); // Состояние для списка заказов

  // Функция для загрузки всех заказов
  const fetchOrders = async () => {
    try {
      const response = await axios.get('/api/orders', {
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('token')}`,
        },
      });
      if (Array.isArray(response.data)) {
        setOrders(response.data); // Записываем полученные заказы в состояние
      }
    } catch (error) {
      console.error('Ошибка при загрузке заказов:', error);
    }
  };

  // Загружаем заказы при монтировании компонента
  useEffect(() => {
    fetchOrders();
  }, []);

  // Если заказы не загружены, отображаем "Загрузка..."
  if (!orders.length) return <div className="text-white">Загрузка...</div>;

  return (
    <div className="find-order-page">
      <div className="container py-5">
        <h2 className="section-title">Найти заказ</h2>
        {orders.length === 0 ? (
          <p className="text-white">Нет доступных заказов.</p>
        ) : (
          <div className="row g-4">
            {orders.map((ord) => (
              <div className="col-md-4" key={ord.id}>
                <div className="order-card">
                  <img
                    src={ord.image || "default-image-url.jpg"} // Если нет картинки, ставим дефолтное изображение
                    alt={ord.title}
                    className="order-image"
                  />
                  <div className="order-info">
                    <h5 className="order-title">{ord.title}</h5>
                    <p className="order-description">{ord.description}</p>
                    <p className="order-price">{ord.price} тг</p>
                    <Link
                      to={`/order/${ord.id}`}
                      className="btn btn-primary"
                    >
                      Подробнее
                    </Link>
                    {isAuthenticated && (
                      <button className="btn btn-primary mt-3">Откликнуться</button>
                    )}
                  </div>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default FindOrder;
