import React, { useState, useEffect } from "react";
import axios from "axios";
import "./ChatWindow.css";

const ChatWindow = ({ orderId, onClose }) => {
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState("");
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  const token = localStorage.getItem("token");

  useEffect(() => {
    const fetchMessages = async () => {
      setLoading(true);
      try {
        const response = await axios.get(`/api/user/order/chat/${orderId}`, {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });

        if (response.data.success) {
          setMessages(response.data.data.messages || response.data.data); // Поддержка разных структур
        } else {
          setError("Не удалось загрузить сообщения.");
        }
      } catch (err) {
        console.error(err);
        setError("Ошибка загрузки чата.");
      } finally {
        setLoading(false);
      }
    };

    if (orderId) fetchMessages();
  }, [orderId, token]);

  const handleSendMessage = async (e) => {
    e.preventDefault();
    if (!newMessage.trim()) return;

    const userId = getUserIdFromToken(token);

    try {
      const response = await axios.post(
        "/api/user/order/chat",
        {
          order_id: orderId,
          user_id: userId,
          message: newMessage,
        },
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );

      if (response.data.success) {
        const newMsg = response.data.data;
        setMessages((prev) => [...prev, newMsg]);
        setNewMessage("");
      } else {
        setError("Не удалось отправить сообщение.");
      }
    } catch (err) {
      console.error(err);
      setError("Ошибка при отправке.");
    }
  };

  return (
    <div className="chat-window">
      <div className="chat-header">
        <h5>Чат по заказу #{orderId}</h5>
        <button className="btn btn-close" onClick={onClose}></button>
      </div>

      <div className="chat-body">
        {loading ? (
          <p>Загрузка...</p>
        ) : (
          <div className="chat-messages">
            {messages.map(msg => (
  <div
    key={msg.id}
    className={msg.sender_id === currentUserId ? "message message-sent" : "message message-received"}
  >
    <strong>{msg.sender_id === currentUserId ? 'Вы' : msg.sender?.name || 'Пользователь'}:</strong>
    <span>{msg.content}</span>
  </div>
))}
          </div>
        )}

        <form onSubmit={handleSendMessage} className="message-form mt-2">
          <input
            type="text"
            className="form-control"
            value={newMessage}
            onChange={(e) => setNewMessage(e.target.value)}
            placeholder="Введите сообщение..."
          />
          <button type="submit" className="btn btn-primary mt-2 w-100">
            Отправить
          </button>
        </form>

        {error && <div className="alert alert-danger mt-2">{error}</div>}
      </div>
    </div>
  );
};

function getUserIdFromToken(token) {
  try {
    const payload = JSON.parse(atob(token.split(".")[1]));
    return payload.sub;
  } catch (e) {
    return null;
  }
}

export default ChatWindow;
