import React, { useState, useEffect } from "react";
import axios from "axios";
import "./ChatWindow.css";

const ChatWindow = ({ orderId, onClose }) => {
  const [messages, setMessages] = useState([]);
  const [content, setContent] = useState("");
  const [chatId, setChatId] = useState(null);
  const token = localStorage.getItem("token");

  useEffect(() => {
    const fetchChat = async () => {
      try {
        const response = await axios.get(`/api/user/order/chat/${orderId}`, {
          headers: { Authorization: `Bearer ${token}` },
        });
        setChatId(response.data.id);
        setMessages(response.data.messages);
      } catch (error) {
        console.error("Ошибка при получении чата:", error);
      }
    };

    fetchChat();
  }, [orderId]);

  const handleSendMessage = async () => {
    if (!content.trim()) return;

    try {
      const response = await axios.post(
        `/api/user/order/chat/${orderId}/message`,
        { content },
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );
      setMessages([...messages, response.data]);
      setContent("");
    } catch (error) {
      console.error("Ошибка при отправке сообщения:", error);
    }
  };

  return (
    <div className="chat-window">
      <div className="chat-header">
        <h5>Чат по заказу #{orderId}</h5>
        <button onClick={onClose}>Закрыть</button>
      </div>
      <div className="chat-body">
        {messages.map((msg) => (
          <div key={msg.id} className={`chat-message ${msg.is_mine ? "mine" : ""}`}>
            <strong>{msg.sender?.name}:</strong> {msg.content}
          </div>
        ))}
      </div>
      <div className="chat-footer">
        <input
          type="text"
          value={content}
          onChange={(e) => setContent(e.target.value)}
          placeholder="Введите сообщение..."
        />
        <button onClick={handleSendMessage}>Отправить</button>
      </div>
    </div>
  );
};

export default ChatWindow;
