import React, { useState, useEffect, useRef } from "react";
import axios from "axios";
import "./ChatWindow.css";

const ChatWindow = ({ orderId, onClose }) => {
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState("");
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const [recipientId, setRecipientId] = useState(null);
  const [chatId, setChatId] = useState(null);

  const token = localStorage.getItem("token");
  const messagesEndRef = useRef(null);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  };

  useEffect(() => {
    const fetchChat = async () => {
      setLoading(true);
      try {
        // Пытаемся получить чат по orderId (предполагаем, что он = chatId)
        const response = await axios.get(`/api/user/order/chat/${orderId}`, {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });

        const chatData = response.data.chat;
        setChatId(chatData.id);

        const fetchedMessages = chatData.messages || [];
        setMessages(fetchedMessages);

        // Определяем recipientId
        const currentUserId = getUserIdFromToken(token);

        if (fetchedMessages.length > 0) {
          const firstIncoming = fetchedMessages.find(
            (msg) => msg.sender_id !== currentUserId
          );

          if (firstIncoming) {
            setRecipientId(firstIncoming.sender_id);
          } else {
            const fallback = fetchedMessages[0];
            const recipient =
              fallback.sender_id === currentUserId
                ? fallback.recipient_id
                : fallback.sender_id;
            setRecipientId(recipient);
          }
        }
      } catch (err) {
        console.error("Ошибка при загрузке чата:", err);
        setError("Ошибка загрузки сообщений.");
      } finally {
        setLoading(false);
      }
    };

    if (orderId && token) {
      fetchChat();
    }
  }, [orderId, token]);

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const handleSendMessage = async (e) => {
    e.preventDefault();
    if (!newMessage.trim()) return;

    try {
      const currentUserId = getUserIdFromToken(token);

      const response = await axios.post(
        `/api/user/order/chat/${orderId}/message`,
        {
          content: newMessage,
          recipient_id: recipientId, // может быть null, но Laravel обработает
        },
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );

      const newMsg = response.data.message;

      if (!recipientId) {
        const recipient =
          newMsg.sender_id === currentUserId
            ? newMsg.recipient_id
            : newMsg.sender_id;
        setRecipientId(recipient);
      }

      setMessages((prev) => [...prev, newMsg]);
      setNewMessage("");
    } catch (err) {
      console.error(err);
      setError("Ошибка при отправке сообщения.");
    }
  };

  return (
    <div className="chat-window">
      <div className="chat-header">
        <h5>Чат по заказу #{orderId}</h5>
        <button className="btn btn-close" onClick={onClose}></button>
      </div>

      <div className="chat-body">
        {loading ? (
          <p>Загрузка...</p>
        ) : (
          <div className="chat-messages">
            {messages.length > 0 ? (
              messages.map((msg) => (
                <div
                  key={msg.id}
                  className={`message ${
                    msg.sender_id === getUserIdFromToken(token)
                      ? "message-sent"
                      : "message-received"
                  }`}
                >
                  <strong>
                    {msg.sender_id === getUserIdFromToken(token)
                      ? "Вы"
                      : msg.sender?.name || `Пользователь #${msg.sender_id}`}{" "}
                    → {msg.recipient?.name || "Получатель"}:
                  </strong>
                  <p>{msg.content}</p>
                  <small className="message-timestamp">
                    {new Date(msg.created_at).toLocaleTimeString([], {
                      hour: "2-digit",
                      minute: "2-digit",
                    })}
                  </small>
                </div>
              ))
            ) : (
              <div className="no-messages">Сообщений пока нет.</div>
            )}
            <div ref={messagesEndRef} />
          </div>
        )}

        <form onSubmit={handleSendMessage} className="message-form mt-2">
          <input
            type="text"
            className="form-control"
            value={newMessage}
            onChange={(e) => setNewMessage(e.target.value)}
            placeholder="Введите сообщение..."
          />
          <button type="submit" className="btn btn-primary mt-2 w-100">
            Отправить
          </button>
        </form>

        {error && <div className="alert alert-danger mt-2">{error}</div>}
      </div>
    </div>
  );
};

function getUserIdFromToken(token) {
  try {
    const payload = JSON.parse(atob(token.split(".")[1]));
    return payload.sub;
  } catch (e) {
    return null;
  }
}

export default ChatWindow;
