import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom"; // ✅ импорт useNavigate
import axios from "axios";
import "bootstrap/dist/css/bootstrap.min.css";
import "./Orders.css";

const Order = () => {
  const { id } = useParams();
  const navigate = useNavigate(); // ✅ инициализация
  const [order, setOrder] = useState(null);
  const [message, setMessage] = useState("");
  const token = localStorage.getItem("token");

  useEffect(() => {
    const fetchOrder = async () => {
      try {
        const res = await axios.get(`/api/orders/${id}`, {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });
        setOrder(res.data);
      } catch (err) {
        console.error("Ошибка при получении заказа:", err);
        setMessage("Не удалось загрузить заказ.");
      }
    };

    fetchOrder();
  }, [id]);

  const handleEdit = () => {
    localStorage.setItem("editOrderId", order.id);           // ✅ сохраняем ID
    localStorage.setItem("editOrderData", JSON.stringify(order)); // (по желанию)
    navigate("/profile/orders"); // ✅ редирект на страницу редактирования
  };

  const handleDelete = async () => {
    if (!window.confirm("Удалить заказ?")) return;

    try {
      await axios.delete(`/api/user/profile/orders/${id}`, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      setMessage("Заказ удалён.");
    } catch (err) {
      console.error("Ошибка при удалении заказа:", err);
      setMessage("Ошибка удаления.");
    }
  };

  if (!order) return <div className="container py-5">Загрузка...</div>;

  return (
    <div className="container py-5">
      <h2 className="section-title">Просмотр заказа</h2>

      {message && <div className="alert alert-info">{message}</div>}

      <div className="order-card">
        <img
          src="https://via.placeholder.com/400x200"
          alt="order"
          className="order-image"
        />
        <div className="order-info">
          <h5 className="order-title">{order.title}</h5>
          <p className="order-description">{order.description}</p>
          <p className="order-price">{order.price} тг</p>

          {order.is_owner && (
            <div className="d-flex justify-content-start mt-3">
              <button className="btn btn-sm btn-warning me-2" onClick={handleEdit}>
                Редактировать
              </button>
              <button className="btn btn-sm btn-danger" onClick={handleDelete}>
                Удалить
              </button>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default Order;
