import React, { useState, useEffect, useRef } from "react";
import axios from "axios";
import "./ChatWindow.css";

const ChatWindow = ({ chatId, onClose }) => {
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState("");
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const [recipient, setRecipient] = useState(null);

  const token = localStorage.getItem("token");
  const messagesEndRef = useRef(null);

  // Прокрутка вниз
  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  };

  // Получение чата
  useEffect(() => {
    const fetchChat = async () => {
      setLoading(true);
      try {
        const response = await axios.get(`/api/user/order/chat/${chatId}`, {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });

        const chatData = response.data.chat;
        const fetchedMessages = chatData.messages || [];
        setMessages(fetchedMessages);

        // Устанавливаем получателя (recipient)
        const currentUserId = getUserIdFromToken(token);
        const firstMessage = fetchedMessages[0];
        const otherUser = firstMessage.sender_id === currentUserId ? firstMessage.recipient : firstMessage.sender;
        setRecipient(otherUser);

      } catch (err) {
        console.error("Ошибка при загрузке чата:", err);
        setError("Ошибка загрузки сообщений.");
      } finally {
        setLoading(false);
      }
    };

    if (chatId && token) {
      fetchChat();
    }
  }, [chatId, token]);

  // Автопрокрутка вниз
  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  // Отправка сообщения
  const handleSendMessage = async (e) => {
    e.preventDefault();
    if (!newMessage.trim()) return;

    try {
      const currentUserId = getUserIdFromToken(token);

      const response = await axios.post(
        `/api/user/order/chat/${chatId}/message`,
        {
          content: newMessage,
          recipient_id: recipient.id, // Используем ID получателя
        },
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );

      const newMsg = response.data.message;
      setMessages((prev) => [...prev, newMsg]);
      setNewMessage("");
    } catch (err) {
      console.error(err);
      setError("Ошибка при отправке сообщения.");
    }
  };

  // Получить userId из токена
  function getUserIdFromToken(token) {
    try {
      const payload = JSON.parse(atob(token.split(".")[1]));
      return payload.sub;
    } catch (e) {
      console.error("Ошибка при декодировании токена:", e);
      return null;
    }
  }

  return (
    <div className="chat-window">
      <div className="chat-header">
        <h5>Чат с {recipient ? `${recipient.firstname} ${recipient.lastname}` : "пользователем"}</h5>
        <button className="btn btn-close" onClick={onClose}></button>
      </div>

      <div className="chat-body">
        {loading ? (
          <p>Загрузка...</p>
        ) : (
          <div className="chat-messages">
            {messages.length > 0 ? (
              messages.map((msg) => (
                <div
                  key={msg.id}
                  className={`message ${
                    msg.sender_id === getUserIdFromToken(token)
                      ? "message-sent"
                      : "message-received"
                  }`}
                >
                  <strong>
                    {msg.sender.firstname} {msg.sender.lastname}:
                  </strong>
                  <p>{msg.content}</p>
                </div>
              ))
            ) : (
              <div className="no-messages">Сообщений пока нет.</div>
            )}
          </div>
        )}

        <form onSubmit={handleSendMessage} className="message-form mt-2">
          <input
            type="text"
            className="form-control"
            value={newMessage}
            onChange={(e) => setNewMessage(e.target.value)}
            placeholder="Введите сообщение..."
          />
          <button type="submit" className="btn btn-primary mt-2 w-100">
            Отправить
          </button>
        </form>

        {error && <div className="alert alert-danger mt-2">{error}</div>}
      </div>

      <div ref={messagesEndRef}></div>
    </div>
  );
};

export default ChatWindow;
