import React, { useEffect, useState } from "react";
import axios from "axios";
import { Link } from "react-router-dom";
import './CartPage.css';

const CartPage = () => {
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchAcceptedOrders = async () => {
      try {
        const res = await axios.get('/api/user/profile/orders/accepted', {
          headers: {
            Authorization: `Bearer ${localStorage.getItem('token')}`,
          },
        });
        setOrders(res.data); // Загружаем принятые заказы
        setLoading(false);
      } catch (error) {
        console.error("Ошибка при получении заказов", error);
        setLoading(false);
      }
    };

    fetchAcceptedOrders();
  }, []);

  if (loading) return <div>Загрузка...</div>;

  return (
    <div className="container py-5">
      <h2>Ваша корзина</h2>
      {orders.length === 0 ? (
        <p>Ваша корзина пуста.</p>
      ) : (
        <div className="row">
          {orders.map((order) => (
            <div key={order.id} className="col-md-4">
              <div className="order-card">
                <img
                  src={order.image || "URL_ДЛЯ_КАРТИНКИ_1"}
                  alt={order.title}
                  className="order-image"
                />
                <div className="order-info">
                  <h5>{order.title}</h5>
                  <p>{order.description}</p>
                  <p><strong>Цена:</strong> {order.price}</p>
                  <Link to={`/profile/orders/${order.id}`} className="btn btn-primary">
                    Подробнее
                  </Link>
                </div>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default CartPage;
