import React, { useState, useEffect } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import "./Home.css";

const Home = () => {
  // Популярные заказы
  const popularOrders = [
    { id: 1, title: "Разработка сайта", description: "by John Doe", price: "150 000 тг" },
    { id: 2, title: "Дизайн логотипа", description: "by Jane Smith", price: "50 000 тг" },
    { id: 3, title: "SEO-продвижение", description: "by Alex Brown", price: "80 000 тг" },
  ];

  // Топ-фрилансеры
  const topFreelancers = [
    { name: "Leighton Kramer", earnings: "307 818 тг" },
    { name: "Haylie Accord",    earnings: "233 333 тг" },
    { name: "Bowen Higgins",    earnings: "201 801 тг" },
  ];

  // Глобальные фрилансеры для слайдера
  const globalFreelancers = [
    { name: "Alice in USA",    earnings: "120 000 тг" },
    { name: "Bob in UK",       earnings: "130 500 тг" },
    { name: "Chen in China",   earnings: "110 300 тг" },
    { name: "Diego in Brazil", earnings: "95 700 тг" },
    { name: "Emma in Germany", earnings: "143 200 тг" },
  ];

  // FAQ
  const faq = [
    {
      q: "Что такое SkillMarket?",
      a: "SkillMarket — это биржа фриланса, где заказчики и исполнители находят друг друга по всему миру быстро и безопасно.",
    },
    {
      q: "Как разместить заказ?",
      a: "Нажмите «Разместить задание» в навбаре, заполните форму и дождитесь откликов от исполнителей.",
    },
    {
      q: "Как оплатить работу?",
      a: "Оплата проходит через безопасный эскроу-счёт: вы переводите деньги сюда, а фрилансер получает их только после приёмки работы.",
    },
    {
      q: "Где найти поддержку?",
      a: "Наш круглосуточный саппорт доступен по кнопке «Помощь» внизу страницы или через чат в боковом меню.",
    },
  ];

  const [openFAQ, setOpenFAQ] = useState(null);

  // Подгружаем Яндекс-API и инициализируем карту
  useEffect(() => {
    const script = document.createElement("script");
    script.src =
      "https://api-maps.yandex.ru/2.1/?apikey=YOUR_YANDEX_API_KEY&lang=ru_RU";
    script.async = true;
    script.onload = () => {
      /* global ymaps */
      ymaps.ready(() => {
        new ymaps.Map("yandex-map", {
          center: [55.76, 37.64], // можно изменить на нужные координаты
          zoom: 1,
          controls: ["zoomControl"],
        });
      });
    };
    document.body.appendChild(script);
  }, []);

  return (
    <div className="home-page">
      {/* HERO */}
      <section className="hero-section">
        <div className="container text-center">
          <h1 className="hero-title">
            Найди и создай крутые проекты с фрилансерами!
          </h1>
          <p className="hero-subtitle">
            Самая большая платформа для поиска исполнителей и заказов.
          </p>
          
          <div className="hero-image-placeholder">
            {<img src="woman.png" alt="Hero"/>}
            
          </div>
        </div>
      </section>

      {/* Yandex Map Section */}
      <section className="map-section">
        <div className="container">
          <h2 className="section-title">Исполнители по всему миру</h2>
          <div id="yandex-map" className="map-container"></div>
        </div>
      </section>

      {/* Global Freelancers Slider */}
      <section className="global-freelancers-section">
        <div className="container">
          <h2 className="section-title">Наши исполнители</h2>
          <div className="slider-track">
            {globalFreelancers
              .concat(globalFreelancers)
              .map((f, i) => (
                <div className="freelancer-card" key={i}>
                  <div className="freelancer-avatar-placeholder">
                    {/* сюда аватар */}
                  </div>
                  <h5 className="freelancer-name">{f.name}</h5>
                  <p className="freelancer-earnings">{f.earnings}</p>
                </div>
              ))}
          </div>
        </div>
      </section>

      {/* Популярные заказы */}
      <section className="popular-section">
        <div className="container">
          <h2 className="section-title">Популярные заказы</h2>
          <div className="orders-list">
            {popularOrders.map((o) => (
              <div className="order-card" key={o.id}>
                <div className="order-image-placeholder">Изображение</div>
                <div className="order-info">
                  <h5 className="order-title">{o.title}</h5>
                  <p className="order-description">{o.description}</p>
                  <p className="order-price">{o.price}</p>
                </div>
              </div>
            ))}
          </div>
        </div>
      </section>

      {/* Топ-фрилансеры */}
      <section className="freelancers-section">
        <div className="container">
          <h2 className="section-title">Топ-фрилансеры</h2>
          <div className="freelancers-list">
            {topFreelancers.map((f, i) => (
              <div className="freelancer-card" key={i}>
                <div className="freelancer-avatar-placeholder">Аватар</div>
                <h5 className="freelancer-name">{f.name}</h5>
                <p className="freelancer-earnings">{f.earnings}</p>
              </div>
            ))}
          </div>
        </div>
      </section>

      {/* FAQ */}
      <section className="faq-section">
        <div className="container">
          <h2 className="section-title">Вопросы и ответы</h2>
          {faq.map((item, idx) => (
            <div
              className={`faq-item ${openFAQ === idx ? "open" : ""}`}
              key={idx}
              onClick={() => setOpenFAQ(openFAQ === idx ? null : idx)}
            >
              <div className="faq-question">
                {item.q}
                <span className="faq-icon">
                  {openFAQ === idx ? "−" : "+"}
                </span>
              </div>
              {openFAQ === idx && (
                <div className="faq-answer">
                  {item.a}
                </div>
              )}
            </div>
          ))}
        </div>
      </section>

      {/* Footer */}
      <footer className="footer-section">
        <div className="container text-center">
          <p className="footer-text">© 2025 SkillMarket. Все права защищены.</p>
        </div>
      </footer>
    </div>
  );
};

export default Home;
